sp_widget.client_script:
function($rootScope,$scope) {
  /* widget controller */
  var c = this;
	c.selectItem =function (idx){
		var id =c.data.notes[idx].sys_id;
		console.log("Note ID" +id);
		$rootScope.noteID =id;
		$rootScope.$emit('selectNote',id);
	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var gr = new GlideRecord("x_snc_createnotes_note");
	gr.addQuery("user",gs.getUser().getID());
	gr.orderByDesc('sys_created_on');
	gr.query();
	while(gr.next()){
		var noteobj ={};

		$sp.getRecordDisplayValues(noteobj,gr,'number,title,sys_id');
		noteobj.note = gr.getValue('note').slice(0,20);
		data.notes.push(noteobj);	

	}
})();
